import React, { useState, useEffect } from 'react';
import MovieList from './components/MovieList';
import Filter from './components/Filter';
import NewMovieForm from './components/NewMovieForm';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import MovieDetails from './components/MovieDetails';

import './App.css';

const films = [
  {
    id: 1,
    title: 'Hitman 2',
    description:
      'Hitman 2 Movies Collection: Hitman + Hitman: Agent 47 (2-Disc)',
    posterURL: 'https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/71lCYeODB6L._SL1324_.jpg',
    rating: 9,
    status: 'Released',
    description2 : 'Product Description, Make the world your weapon Become Agent 47 and dismantle the elusive Shadow Client is militia. Think deadly as you travel the globe to take down your targets in 6 new sandbox environments, improvise each assassination, and explore the franchises most advanced installment to date',
    trailerLink: 'https://www.youtube.com/watch?v=3e4UknKLN-E&t=297s&ab_channel=ELMACHHOUNE'
  },
  {
    id: 2,
    title: 'The Godfather',
    description:
      'An organized crime dynasty transitions from father to son.',
    posterURL: 'https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/515EPW7MZHL._AC_UF894,1000_QL80_.jpg',
    rating: 9.2,
    status: 'Released',
    description2 : 'It is the first installment in The Godfather trilogy, chronicling the Corleone family under patriarch Vito Corleone (Brando) from 1945 to 1955. It focuses on the transformation of his youngest son, Michael Corleone (Pacino), from reluctant family outsider to ruthless mafia boss',
    trailerLink: 'https://www.youtube.com/watch?v=3e4UknKLN-E&t=297s&ab_channel=ELMACHHOUNE'
  },
  {
    id: 3,
    title: 'The Dark Knight',
    description:
      'When the menace known as the Joker wreaks havoc and chaos on the people of Gotham, Batman must accept one of the greatest psychological and physical tests of his ability to fight injustice.',
    posterURL: 'https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/61VhwmtnJvL._AC_UF894,1000_QL80_.jpg',
    rating: 9.0,
    status: 'Released',
    description2 : 'hhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhhh',
    trailerLink: 'https://www.youtube.com/watch?v=3e4UknKLN-E&t=297s&ab_channel=ELMACHHOUNE'
  }
]

const App = () => {
  const [movies, setMovies] = useState(films);
  const [titleFilter, setTitleFilter] = useState('');
  const [ratingFilter, setRatingFilter] = useState('');

  useEffect(() => {
    const storedMovies = JSON.parse(localStorage.getItem('movies'));
    if (storedMovies) {
      setMovies(storedMovies);
    }
  }, []);

  const handleAddMovie = (movie) => {
    setMovies([...movies, movie]);
    localStorage.setItem('movies', JSON.stringify([...movies, movie]));
  };

  useEffect(() => {
    localStorage.setItem('movies', JSON.stringify(movies));
  }, [movies]);

  return (
    <Router>
    <div className="app">
      <h1>Movie App</h1>
      <div className="center_position">
        <Filter
          onFilter={({ title, rating }) => {
            setTitleFilter(title);
            setRatingFilter(rating);
          }}
        />
        <NewMovieForm onAddMovie={handleAddMovie} />
      </div>
      <Routes>
        <Route exact path="/" element={
          <MovieList
            movies={movies.filter((movie) => {
              return (
                movie.title.toLowerCase().includes(titleFilter.toLowerCase()) &&
                movie.rating >= ratingFilter
              );
            })}
          />
        } />
        <Route path="/movies/:id" element={<MovieDetails movies={movies} />} />
      </Routes>
    </div>
  </Router>
  );
};

export default App;
