import React, { useState } from 'react';

const NewMovieForm = ({ onAddMovie }) => {
  const [title, setTitle] = useState('');
  const [posterURL, setPosterURL] = useState('');
  const [rating, setRating] = useState('');
  const [status, setStatus] = useState('');
  const [description, setDescription] = useState('');
  const [trailerLink, setTrailerLink] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    // Create a new movie object
    const newMovie = {
      title: title,
      posterURL: posterURL,
      rating: rating,
      status: status,
      description: description,
      trailerLink: trailerLink
    };

    // Call the onAddMovie function passed down from the parent component
    onAddMovie(newMovie);

    // Clear the form fields
    setTitle('');
    setPosterURL('');
    setRating('');
    setStatus('');
    setDescription('');
    setTrailerLink('');
  };

  return (
    <div className="new-movie-form">
      <h2>Add New Movie</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" value={title} onChange={(event) => setTitle(event.target.value)} />
        </label>
        <label>
          Poster URL:
          <input type="text" value={posterURL} onChange={(event) => setPosterURL(event.target.value)} />
        </label>
        <label>
          Rating:
          <input type="number" step="0.1" min="0" max="10" value={rating} onChange={(event) => setRating(event.target.value)} />
        </label>
        <label>
          Status:
          <input type="text" value={status} onChange={(event) => setStatus(event.target.value)} />
        </label>
        <label>
          Description:
          <textarea value={description} onChange={(event) => setDescription(event.target.value)} />
        </label>
        <label>
          Trailer Link:
          <input type="text" value={trailerLink} onChange={(event) => setTrailerLink(event.target.value)} />
        </label>
        <button type="submit">Add Movie</button>
      </form>
    </div>
  );
};

export default NewMovieForm;
